import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import NavBar from "./NavBar";
import SocialBar from "./SocialBar";
import VideoSection from "./VideoSection";
import TitleSection from "./TitleSection ";
import CommentSection from "./CommentSection";



class App extends Component {
  state = { 
    videolist: [
    {id: "1234",
     name: "IAN @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    }
  ]
};
  render() {
    return (
      <div className="App">
            <NavBar/>
            <SocialBar/>
            <main>
              <section className="mainbar">
               <VideoSection/>
               <TitleSection></TitleSection>
               <CommentSection></CommentSection>
              </section>
              <VideoList videosArray={this.state.videolist} ></VideoList>
            </main>      
      </div>
    );
  }
}




class VideoList extends React.Component {
  render() {

    let videos= this.props.videosArray;
    const videoElements= videos.map((video) => {
      return <SingleVideo
      id= {video.id}
      name= {video.name}
      username= {video.username}
      views= {video.views}
      src= {video.src}
      ></SingleVideo>
  });

   return (
       <section className="listvideo">
        <h5>Up Next</h5>
        {videoElements}
       </section>
    );
  }
}
class SingleVideo extends Component {
  render() {
      return (
          <div className="singlevideo__section">
              <div className="singlevideo__section--content">
                  <img src={this.props.src}></img>
              </div>
              <div className="singlevideo__section--description">
                  <h4>{this.props.name}</h4>
                  <p className="user">{this.props.username}</p>
                  <p>{this.props.views}</p>
              </div>
          </div>
      );
  }
}
export default App;
