import React, { Component } from 'react';
import VideoSection from "./pages/VideoSection";
import TitleSection from "./pages/TitleSection ";
import CommentSection from "./pages/CommentSection";
import VideoList from "./pages/VideoList";

const API_KEY = '406d1d67-cd68-4d5f-be46-11656b50339d';
const baseUrl = 'https://project-2-api.herokuapp.com/';
const endpoint= "videos";
const queryString= "?api_key=" + API_KEY;

class Main extends Component {
    state = {
        serverdata: [],
    }
    //GET REQUEST
    componentDidMount() {
        fetch(baseUrl+ endpoint + queryString)
        .then(response  => response.json())
        //now we have data
        .then (data => {
        console.log(data);
        //now we wanna put restate=> set state
        this.setState({
        serverdata: data,
        })
      })
        .catch( error => {
          console.log(error)
        })
    } 

    render() {

      return (
        <main>
                <section className="mainbar">
                  <VideoSection/>
                  <TitleSection videosArray={this.state.serverdata}></TitleSection>
                  <CommentSection videosArray={this.state.serverdata}></CommentSection>
                </section>
                  <VideoList videosArray={this.state.serverdata} ></VideoList>
        </main> 
      );
    }
  }
export default Main;
