import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';



class App extends Component {
  state = { 
    videolist: [
    {id: "1234",
     name: "IAN @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    }
  ]
};



  render() {
    return (
      <div className="App">
            <NavBar/>
            <SocialBar/>
            <main>
              <section className="mainbar">
               <VideoSection/>
               <TitleSection></TitleSection>
               <CommentSection></CommentSection>
              </section>
              <VideoList videosArray={this.state.videolist} ></VideoList>
            </main>      
      </div>
    );
  }
}

class NavBar extends React.Component {
  render() {

    return (
      <header class="header">
            <div class="header__logo">
            <img className="logo-photo" src="./Assets/Icons/BrainFlix Logo.svg"></img>
            </div>
            <form class="header__links header__links--right">
              <input class="search" placeholder="Search..." />
              <button class="search-button"><img src="/Assets/Icons/Search.svg"></img></button>
            </form>
            <img className="upload-video" src="/Assets/Icons/Content Upload.svg"></img>
            <img className="user" src="/Assets/Images/donaldson.jpg" ></img>
