import React, { Component } from 'react'
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts'


export default class Player extends Component {
    renderIframe(spotifyData) {
        if (spotifyData != null) {
          let randomresult=  getRandomInt(spotifyData.body.playlists.items.length - 1)
            let id = spotifyData.body.playlists.items[randomresult].id;
            let title = spotifyData.body.playlists.items[randomresult].name;
            return <div className="playpage"> 
                <h1>{title}</h1>
                <iframe allow="geolocation" src={`https://open.spotify.com/embed/user/spotify/playlist/${id}`} width="700" height="680" frameborder="1" allowtransparency="true"></iframe> 
            </div>
        }
    }
  render() {
    let stats= this.props.data;
    console.log(stats.microsoft[0].faceAttributes.emotion);
    // let data = [
    //   { name: 'anger', value: stats.microsoft[0].faceAttributes.emotion.anger },
    //   { name: 'contempt', value: stats.microsoft[0].faceAttributes.emotion.contempt},
    //   { name: 'disgust', value: stats.microsoft[0].faceAttributes.emotion.disgust },
    //   { name: 'fear', value: stats.microsoft[0].faceAttributes.emotion.fear},
    //   { name: 'hapiness', value: stats.microsoft[0].faceAttributes.emotion.hapiness },
    //   { name: 'neutral', value: stats.microsoft[0].faceAttributes.emotion.neutral },
    //   { name: 'sadness', value: stats.microsoft[0].faceAttributes.emotion.sadness },
    //   { name: 'surprise', value: stats.microsoft[0].faceAttributes.emotion.surprise },
    // ]
    return (
      <div className="player">
        <div class="overplay">
            {/* <img className="playbutton" src="https://hanslodge.com/images/6Tp5aga7c.png" alt="Video thumbnail" /> */}
            <img className="mainimage" src= {this.props.imgsrc}/>
            {/* <BarChart width={730} height={250} 
            data={data}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="pv" fill="#8884d8" />
                <Bar dataKey="uv" fill="#82ca9d" />
            </BarChart> */}
        </div>        
        { this.renderIframe(stats.spotify) }
      </div>
    )
  }
}
function getRandomInt(max) {
    return Math.floor(Math.random() * Math.floor(max));
  }
