import React, { Component } from 'react';
import {Link} from "react-router-dom";

export default class Upload extends Component {

    renderIframe(spotifyData) {
        if (spotifyData != null) {
          let randomresult=  getRandomInt(spotifyData.body.playlists.items.length - 1)
            let id = spotifyData.body.playlists.items[randomresult].id;
            let title = spotifyData.body.playlists.items[randomresult].name;
            // console.log(spotifyData.body.playlists)
            // console.log(spotifyData.body.playlists.items[0].id);
            return <div> 
                <h1>{title}</h1>
                <iframe allow="geolocation" src={`https://open.spotify.com/embed/user/spotify/playlist/${id}`} width="700" height="680" frameborder="1" allowtransparency="true"></iframe> 
            </div>
        }
    }
  render() {
    let stats= this.props.data;
    return (
    <div className="upload">
        <div class="search">
        <input type="text" placeholder=" "></input>
        <div>
            <svg>
                <use href="#path"></use>
            </svg>
        </div>
    </div>

    <svg xmlns="http://www.w3.org/2000/svg" style="display: none;">
    <symbol xmlns="http://www.w3.org/2000/svg" viewBox="0 0 160 28" id="path">
        <path d="M32.9418651,-20.6880772 C37.9418651,-20.6880772 40.9418651,-16.6880772 40.9418651,-12.6880772 C40.9418651,-8.68807717 37.9418651,-4.68807717 32.9418651,-4.68807717 C27.9418651,-4.68807717 24.9418651,-8.68807717 24.9418651,-12.6880772 C24.9418651,-16.6880772 27.9418651,-20.6880772 32.9418651,-20.6880772 L32.9418651,-29.870624 C32.9418651,-30.3676803 33.3448089,-30.770624 33.8418651,-30.770624 C34.08056,-30.770624 34.3094785,-30.6758029 34.4782612,-30.5070201 L141.371843,76.386562" transform="translate(83.156854, 22.171573) rotate(-225.000000) translate(-83.156854, -22.171573)"></path>
    </symbol>
   </svg>
    

        <form  onSubmit={this.props.submitform} className="comments__form" id = "comment-form" action="" >
            <input type="text" name="comment" placeholder="Enter the image address"/>
            <div className="comments__form--button">
                <button className="cancel" type="reset">CANCEL</button>
                <button type="submit">SUBMIT</button>
            </div>
            <img src= {this.props.imgdisplay}/>
        </form>
        <img id="photo"/>
        { this.renderIframe(stats.spotify) }
    </div>
    )
  }
}

function getRandomInt(max) {
    return Math.floor(Math.random() * Math.floor(max));
  }
