import React, { Component } from 'react';

class VideoSection extends  Component {
    state = {
        serverdata: [],
    }
    //GET REQUEST
    componentDidMount() {
        fetch("https://project-2-api.herokuapp.com/videos/1edc16bd-1bad-418b-bd40-c72ddd926672?api_key=406d1d67-cd68-4d5f-be46-11656b50339d")
        .then(response  => response.json())
        //now we have data
        .then (data => {
        console.log(data);
        //now we wanna put restate=> set state
        this.setState({
        serverdata: data,
        })
      })
        .catch( error => {
          console.log(error)
        })
    } 
    render() {

      return (
        <section className="video__section">
          <video
            controls poster="/Assets/Images/maxresdefault.jpg"
            src="/Assets/BrainStation Sample Video.mp4" 
            type="video/mp4">
            Your browser does not support HTML5 video.
          </video>

          <p>{this.serverdata.title}</p>
          <div className="video__section--comments">
            <p className="video__section--views">2,304,189 views</p>
            <div className="video__section--icons">
              <div>
                <img className="up" src="/Assets/Icons/Thumbs Up.svg" ></img>
                <h5>6000</h5>              
              </div>
              <div>
                <img className="down" src="/Assets/Icons/Thumbs Down.svg" ></img>
                <h5>40</h5>
              </div>
              <div>
                <img className="share" src="/Assets/Icons/Share.svg"></img>
                <h5>SHARE</h5>
              </div>
            </div>
          </div>
        </section>
      );
    }
  }

export default VideoSection;
