import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import NavBar from "./pages/NavBar";
import SocialBar from "./pages/SocialBar";
import VideoSection from "./pages/VideoSection";
import TitleSection from "./pages/TitleSection ";
import CommentSection from "./pages/CommentSection";
import VideoList from "./pages/VideoList";
// import Fetching from "./pages/Fetching";

class App extends Component {

  state = {
      serverdata: [],
  }
  //GET REQUEST
  componentDidMount() {
      fetch(baseUrl+ endpoint + queryString)
      .then(response  => response.json())
      .then (data => {
      console.log(data);
      //now we wanna put in the sate=> set state
      this.setState({
      //now we have data
      serverdata: data,
      })
      console.log(serverdata);
  });
  } 

  render() {


    return (
      <div className="App">
            <NavBar/>
            <SocialBar/>

            <main>
              <section className="mainbar">
               <VideoSection/>
               <TitleSection commentsArray={this.state.videolist}></TitleSection>
               <CommentSection videosArray={this.state.videolist}></CommentSection>
              </section>
              {/* <Fetching></Fetching> */}
              <VideoList videosArray={this.state.serverdata} ></VideoList>
            </main>      
      </div>
    );
  }
}
export default App;
