import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import NavBar from "./NavBar";
import SocialBar from "./SocialBar";
import VideoSection from "./VideoSection";



class App extends Component {
  state = { 
    videolist: [
    {id: "1234",
     name: "IAN @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    },
    {id: "1234",
     name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
     username: "MLB",
     views: "1.1M likes",
     src: "./Assets/Images/donaldson.jpg"
    }
  ]
};
  render() {
    return (
      <div className="App">
            <NavBar/>
            <SocialBar/>
            <main>
              <section className="mainbar">
               <VideoSection/>
               <TitleSection></TitleSection>
               <CommentSection></CommentSection>
              </section>
              <VideoList videosArray={this.state.videolist} ></VideoList>
            </main>      
      </div>
    );
  }
}


class TitleSection extends React.Component {
  render() {
    return (
      <section className="title__section">
      <img className="user" src="/Assets/Images/donaldson.jpg" ></img>
      <div className="title__section--description">
        <div className="title__section--username">
          <div>
              <p><span>MLB</span></p>
              <h5>Published on Oct 14, 2015</h5>
          </div>
          <button className="subscribe">SUBSCRIBE<p>2.3M</p></button>
        </div>

        <p>10/14/15: Jose Bautista crushes a long go-ahead three-run homer in the 7th inning of ALDS Game 5</p>
        <button className="showmore"><h5>SHOW MORE</h5></button>
      </div>

      </section>
    );
  }
}

class CommentSection extends React.Component {
  render() {
    return (
      <section className="comments__section">
      <img className="user" src="/Assets/Images/donaldson.jpg" ></img>
      <form className="comments__form" id = "comment-form" action="">
            <input type="text" name="FirstName" placeholder="Add a public comment"/>
            <div class="comments__form--button">
              <button className="cancel" type="click">CANCEL</button>
              <button type="submit">COMMENT</button>
            </div>
      </form>
      </section>
    );
  }
}
class VideoList extends React.Component {
  render() {

    let videos= this.props.videosArray;
    const videoElements= videos.map((video) => {
      return <SingleVideo
      id= {video.id}
      name= {video.name}
      username= {video.username}
      views= {video.views}
      src= {video.src}
      ></SingleVideo>
  });

   return (
       <section className="listvideo">
        <h1>Up Next</h1>
        {videoElements}
       </section>
    );
  }
}
class SingleVideo extends Component {
  render() {
      return (
          <div className="singlevideo__section">
              <div className="singlevideo__section--content">
                  <img src={this.props.src}></img>
              </div>
              <div className="singlevideo__section--description">
                  <h5>{this.props.name}</h5>
                  <p className="user">{this.props.username}</p>
                  <p>{this.props.views}</p>
              </div>
          </div>
      );
  }
}
export default App;
