import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';


class App extends Component {
  render() {
    return (
      <div className="App">
            <NavBar/>
            <main>
              <section className="mainbar">
               <VideoSection/>
               <TitleSection></TitleSection>
               <CommentSection></CommentSection>
              </section>
              <VideoList></VideoList>
            </main>      
      </div>
    );
  }
}

class NavBar extends React.Component {
  render() {

    return (
      <header class="header">
            <div class="header__logo">
            <img className="logo-photo" src="./Assets/Icons/BrainFlix Logo.svg"></img>
            </div>
            <form class="header__links header__links--right">
              <input class="search" placeholder="Search..." />
              <button class="search-button"><img src="/Assets/Icons/Search.svg"></img></button>
            </form>
            <img className="upload-video" src="/Assets/Icons/Content Upload.svg"></img>
            <img className="user" src="/Assets/Images/donaldson.jpg" ></img>
      </header>
    );
  }
}


class VideoSection extends  React.Component {
  render() {

    return (
      <section className="video__section">
          <video 
          controls
          src="/Assets/BrainStation Sample Video.mp4" 
          type="video/mp4">
          Your browser does not support HTML5 video.
        </video>

        <p>Jose Bautista hammers go-ahead three-run shot in ALDS Game 5, delivers epic bat flip</p>
        <div className="video__section--comments">
          <p className="video__section--views">2,304,189 views</p>
          <div className="video__section--icons">
            <div>
              <img className="up" src="/Assets/Icons/Thumbs Up.svg" ></img>
              <p>6000</p>
            </div>
            <div>
              <img className="down" src="/Assets/Icons/Thumbs Down.svg" ></img>
              <p>40</p>
            </div>
            <div>
              <img className="share" src="/Assets/Icons/Share.svg"></img>
              <p>SHARE</p>

            </div>
          </div>
        </div>
      </section>
    );
  }
}


class TitleSection extends React.Component {
  render() {
    return (
      <section className="title__section">
      <img className="user" src="/Assets/Images/donaldson.jpg" ></img>
      <div className="title__section--description">
        <div className="title__section--username">
          <div>
              <p><span>MLB</span></p>
              <h5>Published on Oct 14, 2015</h5>
          </div>
          <button className="subscribe">SUBSCRIBE<p>2.3m</p></button>
        </div>

        <p>10/14/15: Jose Bautista crushes a long go-ahead three-run homer in the 7th inning of ALDS Game 5</p>
        <button className="showmore">SHOW MORE</button>
      </div>

      </section>
    );
  }
}

class CommentSection extends React.Component {
  render() {
    return (
      <section className="comments__section">
      <img className="user" src="/Assets/Images/donaldson.jpg" ></img>
      <form className="comments__form" id = "comment-form" action="">
            <input type="text" name="FirstName" placeholder="Add a public comment"/>
            <div class="comments__form--button">
              <button className="cancel" type="click">CANCEL</button>
              <button type="submit">COMMENT</button>
            </div>
      </form>
      </section>
    );
  }
}




class VideoList extends React.Component {
  render() {
    const videos = [
      {id: "1234",
       name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
       username: "MLB",
       views: "1.1M likes",
       src: "./Assets/Images/donaldson.jpg"
      },
      {id: "1234",
       name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
       username: "MLB",
       views: "1.1M likes",
       src: "./Assets/Images/donaldson.jpg"
      },
      {id: "1234",
       name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
       username: "MLB",
       views: "1.1M likes",
       src: "./Assets/Images/donaldson.jpg"
      },
      {id: "1234",
       name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
       username: "MLB",
       views: "1.1M likes",
       src: "./Assets/Images/donaldson.jpg"
      },
      {id: "1234",
       name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
       username: "MLB",
       views: "1.1M likes",
       src: "./Assets/Images/donaldson.jpg"
      },
      {id: "1234",
       name: "TEX @ TOR Gm5: Blue Jays take lead in wild 7th inning",
       username: "MLB",
       views: "1.1M likes",
       src: "./Assets/Images/donaldson.jpg"
      }
    ];

    const videoElements= videos.map((video) => {
      return <SingleVideo
      id= {video.id}
      name= {video.name}
      username= {video.username}
      views= {video.views}
      src= {video.src}
      ></SingleVideo>
  });

   return (
       <section className="listvideo">
        <h1>Up Next</h1>
        {videoElements}
       </section>
    );
  }
}



class SingleVideo extends Component {
  render() {
      return (
          <div className="singlevideo__section">
              <div className="singlevideo__section--content">
                  <img src={this.props.src}></img>
              </div>
              <div className="singlevideo__section--description">
                  <h4>{this.props.name}</h4>
                  <p>{this.props.username}</p>
                  <p>{this.props.views}</p>
              </div>
          </div>
      );
  }
}






export default App;
