import React, { Component } from 'react'

export default class TakePicture extends Component {

  render() {
      const mediaConstraints = {
        video: true
      }
      const subscriptionKey = 'ce2a75dde3b84a55b087b165838c69c6';
      let globalStream;

      navigator.mediaDevices.getUserMedia(mediaConstraints)
      .then(function(stream) {
        onStream(stream);
      })
      .catch(function(err) {
        console.error(err);
      });

      function onStream(stream) {
        globalStream = stream;
        startLiveVideo(stream);
      }

      function startLiveVideo(stream) {
        const videoElem = document.getElementById('live-feed');
        videoElem.srcObject = stream;
        videoElem.play();
      }

      function takeSnapshot() {
        const track = globalStream.getVideoTracks()[0];
        imageCapture = new ImageCapture(track);
        imageCapture.takePhoto().then(blob => {
          const imgElem = document.getElementById('snapshot');
          imgElem.src = URL.createObjectURL(blob);
          postImage(blob);
        });
      }

    return (
      <div className="takepicture">
        <video id="live-feed"></video>
        <img id="snapshot"/>
        <button button="button" onclick="takeSnapshot()">Take Snapshot</button>
      </div>
    )
  }
}
